document.head.insertAdjacentHTML('beforeend', `<template id="moon-phase"><style>svg{width:200px;transform-origin:50% 50%;background:transparent}#r1,#l0{fill:var(--background, #222)}#r0,#l1{fill:var(--color, #ccc)}</style><svg viewbox="0 0 100 100" preserveaspectratio="xMinYMin"><path id="r0"/><path id="r1"/><path id="l0"/><path id="l1"/></svg></template>`);window.customElements.define('moon-phase',class extends HTMLElement{constructor(){super(),this.attachShadow({mode:'open'}).appendChild(document.querySelector('template#moon-phase').content.cloneNode(true));;}$(a){return this.shadowRoot.querySelector(a)}static get observedAttributes(){return['value']}attributeChangedCallback(){var a=this.getAttribute('value')*1;var b=a<50?0:180;if(a){var c=0;this.hasAttribute('A')&&(c=90),this.hasAttribute('S')&&(c=180);var d=c+b;var e=a<50?a*2:200-a*2;var f=0;var g=0;e>50?g=e-50:f=50-e,void 0,this.$('svg').setAttribute('style',`transform: rotate(${d}deg);`),this.setPath('r0',50,1),this.setPath('r1',f,1),this.setPath('l0',50,0),this.setPath('l1',g,0)}}setPath(a,b,c){this.$('#'+a).setAttribute('d',`M50,0 A${b},50 0,0,${c} 50,100 z`)}});